mapper.select:
select distinct a.id, a.name, a.start_time, a.activity_type, a.activity_aspect, a.state, a.score, a.comment
from fu_activity a


        a.name = #{activityName}


        and a.state = #{activityState}


        and a.activity_type = #{activityType}


        and a.activity_aspect = #{activityAspect}


        and (a.grade = #{grade})


        and 
            #{classId}


    and a.deleted = 0

mapper.update:
update fu_activity
set deleted = 1
where id = #{activityId}

update fu_activity
set score = #{averageScore}
where id = #{actId}

mapper.select:
select count(*)
from fu_activity
where start_time between #{start} and #{end}
