mapper.update:
update fu_scale_content
title = #{title}, grade = #{grade}, domain = #{domain},
where id = #{scaleId}

update `fu_item_content`
set pre_item = #{scaleContent.itemContent}
where pre_item_id = #{scaleContent.itemId}
  and scale_id = #{scaleContent.scaleId}

mapper.select:
select *,id scale_id from fu_scale_content

    creator_id=#{creatorId}
    and title like concat('%',#{title},'%')
    and create_date like concat('%',#{createDate},'%')
    and state=#{state}

select * from fu_execution

    and exec_id = #{execId}
    and grade_id = #{gradeId}
    and scale_id = #{scaleId}

SELECT sc.*, sc.id AS scale_id, bt.teacher_name AS creatorName, (SELECT COUNT(*) FROM fu_item_content fic WHERE fic.scale_id = sc.id) AS hasChildren FROM fu_scale_content sc LEFT JOIN basic_teacher bt ON sc.creator_id = bt.id
AND sc.title LIKE CONCAT('%', #{title}, '%')
AND bt.teacher_name LIKE CONCAT('%', #{creatorName}, '%')
AND sc.state = #{state}
AND sc.create_date = #{createDate}

select * from fu_scale_content

    and creator_id = #{creatorId}
    and title like concat('%',#{title},'%')
    and create_date like concat('%',#{createDate},'%')
    and state = #{state}

order by create_date desc
